import { logger } from '@polkadot/util';
import { extractTypes } from "./extractTypes.js";
import { flattenUniq } from "./flattenUniq.js";
var l = logger('metadata');
/** @internal */

export function validateTypes(registry, types, throwError) {
  var missing = flattenUniq(extractTypes(types)).filter(function (type) {
    return !registry.hasType(type);
  });

  if (missing.length !== 0) {
    var message = "Unknown types found, no types for ".concat(missing.join(', '));

    if (throwError) {
      throw new Error(message);
    } else {
      l.warn(message);
    }
  }
}
