import { combineLatest, of } from '@polkadot/x-rxjs';
import { memo } from "../util/index.js";
export function votingBalances(instanceId, api) {
  return memo(instanceId, function (addresses) {
    return !addresses || !addresses.length ? of([]) : combineLatest(addresses.map(function (accountId) {
      return api.derive.balances.account(accountId);
    }));
  });
}
