import { map } from '@polkadot/x-rxjs/operators';
import { memo } from "../util/index.js";
import { parseImage } from "./util.js";
export function preimages(instanceId, api) {
  return memo(instanceId, function (hashes) {
    return api.query.democracy.preimages.multi(hashes).pipe(map(function (images) {
      return images.map(function (imageOpt) {
        return parseImage(api, imageOpt);
      });
    }));
  });
}
