/**
 * Given an u8a, and an array of Type constructors, decode the u8a against the
 * types, and return an array of decoded values.
 *
 * @param u8a - The u8a to decode.
 * @param types - The array of Constructor to decode the U8a against.
 */
export function decodeU8a(registry, u8a, _types) {
  var types = Array.isArray(_types) ? _types : Object.values(_types);
  var result = [];
  var offset = 0;

  for (var i = 0; i < types.length; i++) {
    var value = new types[i](registry, u8a.subarray(offset));
    result.push(value);
    offset += value.encodedLength;
  }

  return result;
}
