export var HARDENED = 0x80000000;
export function hdValidatePath(path) {
  if (!path.startsWith('m/')) {
    return false;
  }

  var parts = path.split('/').slice(1);
  return parts.every(function (n) {
    return /^\d+'?$/.test(n);
  }) && !parts.map(function (n) {
    return parseInt(n.replace("'", ''), 10);
  }).some(function (n) {
    return isNaN(n) || n >= HARDENED || n < 0;
  });
}
