import { switchMap } from '@polkadot/x-rxjs/operators';
import { memo } from "../util/index.js";
/**
 * @description Retrieve the staking overview, including elected and points earned
 */

export function currentPoints(instanceId, api) {
  return memo(instanceId, function () {
    return api.derive.session.indexes().pipe(switchMap(function (_ref) {
      var activeEra = _ref.activeEra;
      return api.query.staking.erasRewardPoints(activeEra);
    }));
  });
}
