import { sr25519KeypairFromSeed } from '@polkadot/wasm-crypto';
import { schnorrkelKeypairFromU8a } from "./fromU8a.js";
/**
 * @name schnorrkelKeypairFromSeed
 * @description Returns a object containing a `publicKey` & `secretKey` generated from the supplied seed.
 */

export function schnorrkelKeypairFromSeed(seed) {
  return schnorrkelKeypairFromU8a(sr25519KeypairFromSeed(seed));
}
