import { map, switchMap } from '@polkadot/x-rxjs/operators';
import { memo } from "../util/index.js";
export function referendumsFinished(instanceId, api) {
  return memo(instanceId, function () {
    return api.derive.democracy.referendumIds().pipe(switchMap(function (ids) {
      return api.query.democracy.referendumInfoOf.multi(ids);
    }), map(function (infos) {
      return infos.filter(function (optInfo) {
        return optInfo.isSome;
      }).map(function (optInfo) {
        return optInfo.unwrap();
      }).filter(function (info) {
        return info.isFinished;
      }).map(function (info) {
        return info.asFinished;
      });
    }));
  });
}
