import { of } from '@polkadot/x-rxjs';
import { map } from '@polkadot/x-rxjs/operators';
import { memo } from "../util/index.js";
/**
 * @name indexToId
 * @param {( AccountIndex | string )} accountIndex - An accounts index in different formats.
 * @returns Returns the corresponding AccountId.
 * @example
 * <BR>
 *
 * ```javascript
 * api.derive.accounts.indexToId('F7Hs', (accountId) => {
 *   console.log(`The AccountId of F7Hs is ${accountId}`);
 * });
 * ```
 */

export function indexToId(instanceId, api) {
  return memo(instanceId, function (accountIndex) {
    return api.query.indices ? api.query.indices.accounts(accountIndex).pipe(map(function (optResult) {
      return optResult.unwrapOr([])[0];
    })) : of(undefined);
  });
}
