import { of } from '@polkadot/x-rxjs';
import { switchMap } from '@polkadot/x-rxjs/operators';
import { memo } from "../util/index.js";
export function referendumsActive(instanceId, api) {
  return memo(instanceId, function () {
    return api.derive.democracy.referendumIds().pipe(switchMap(function (ids) {
      return ids.length ? api.derive.democracy.referendumsInfo(ids) : of([]);
    }));
  });
}
