import { assert } from '@polkadot/util';
import { DeriveJunction } from "./DeriveJunction.js";
var RE_JUNCTION = /\/(\/?)([^/]+)/g;
/**
 * @description Extract derivation junctions from the supplied path
 */

export function keyExtractPath(derivePath) {
  var parts = derivePath.match(RE_JUNCTION);
  var path = [];
  var constructed = '';

  if (parts) {
    constructed = parts.join('');
    parts.forEach(function (value) {
      path.push(DeriveJunction.from(value.substr(1)));
    });
  }

  assert(constructed === derivePath, "Re-constructed path \"".concat(constructed, "\" does not match input"));
  return {
    parts: parts,
    path: path
  };
}
